SELECT nombre FROM ( SELECT Producto_CodigoDeBarras FROM (((SELECT Proveedor_nit from "+ pp.darTablaPedido()+" WHERE EXTRACT(month from fecha)>2) natural join (SELECT nit as Proveedor_nit FROM " + pp.darTablaProveedor()+" )) NATURAL JOIN (select proveedor_nit, Producto_CodigoDeBarras FROM "+pp.darTablaProvee()+" )) INNER JOIN (SELECT codigoDeBarras, nombre from "+pp.darTablaProducto()+") ON Producto_CodigoDeBarras= codigoDeBarras)